class UI{constructor(){this.CANVAS=document.getElementById("canvas"),this.DATA=document.getElementById("data"),this.RGB_ARR=[]}randomColor(){let e=Math.floor(256*Math.random()),t=Math.floor(256*Math.random()),n=Math.floor(256*Math.random());this.CANVAS.style.backgroundColor=`rgb(${e},${t},${n})`,this.RGB_ARR[0]=e,this.RGB_ARR[1]=t,this.RGB_ARR[2]=n}setSelector(){let e=document.getElementById("red-input"),t=document.getElementById("green-input"),n=document.getElementById("blue-input");e.value=this.RGB_ARR[0],t.value=this.RGB_ARR[1],n.value=this.RGB_ARR[2];let r=document.getElementById(`rMarker-${this.RGB_ARR[0]}`);for(let e=0;e<256;e++)document.getElementById(`rMarker-${e}`).classList.remove("show","rMarker-selected");r.classList.add("show","rMarker-selected"),console.log(document.getElementById(`rMarker-${this.RGB_ARR[0]}`))}clearData(){for(this.DATA.classList.remove("show");this.DATA.hasChildNodes();)this.DATA.removeChild(this.DATA.childNodes[0])}getData(){this.DATA.classList.add("show"),this.getRgbData(),this.getHexData(),this.getHslData()}getRgbData(){let e=document.createElement("strong");e.innerHTML="RGB: ",this.DATA.appendChild(e);const t=document.createElement("div");t.innerHTML=this.CANVAS.style.backgroundColor,this.DATA.appendChild(t)}getHexData(){let e=this.RGB_ARR[0].toString(16).toUpperCase();1===e.length&&(e="0"+e);let t=this.RGB_ARR[1].toString(16).toUpperCase();1===t.length&&(t="0"+t);let n=this.RGB_ARR[2].toString(16).toUpperCase();1===n.length&&(n="0"+n);const r=document.createElement("strong");r.innerHTML="HEX: ",this.DATA.appendChild(r);const d=document.createElement("div");d.innerHTML="#"+e+t+n,this.DATA.appendChild(d)}getHslData(){const e=this.RGB_ARR[0]/255,t=this.RGB_ARR[1]/255,n=this.RGB_ARR[2]/255,r=Math.max(e,t,n),d=Math.min(e,t,n),s=r-d;let i;0===s?i=0:r===e?(i=(t-n)/s%6*60)<0&&(i+=360):r===t?(i=60*((n-e)/s+2))<0&&(i+=360):(i=60*((e-t)/s+4))<0&&(i+=360);let l,a=(r+d)/2;l=0===s?0:s/(1-Math.abs(2*a-1));const o=i.toFixed(0),c=(100*l).toFixed(0),g=(100*a).toFixed(0),h=document.createElement("strong");h.innerHTML="HSL: ",this.DATA.appendChild(h);const u=document.createElement("div");u.innerHTML=`hsl(${o}, ${c}%, ${g}%)`,this.DATA.appendChild(u)}}function createSelectors(){const e=document.getElementById("red-selector");let t='\n    <div class="grid-wrapper">\n      <div class="grid-title">\n        <strong>Red:</strong>\n      </div>\n      <div class="grid-input">\n        <input id="red-input" value="" placeholder="Enter a value between 0-255" />\n      </div>\n      <div class="grid-slider">\n        <table id="red-slider" class="slider">\n          <tr>\n  ';for(let e=0;e<256;e++)t+=`\n      <td id="red-${e}" style="background-color:rgb(${e},0,0);">\n        <div id="rMarker-${e}" class="rMarker"></div>\n      </td>`;t+="</tr></table></div></div>",e.innerHTML=t;const n=document.getElementById("green-selector");let r='\n    <div class="grid-wrapper">\n      <div class="grid-title">\n        <strong>Green:</strong>\n      </div>\n      <div class="grid-input">\n        <input id="green-input" value="" placeholder="Enter a value between 0-255" />\n      </div>\n      <div class="grid-slider">\n        <table id="green-slider" class="slider">\n          <tr>\n  ';for(let e=0;e<256;e++)r+=`<td style="background-color:rgb(0,${e},0);"></td>`;r+="</tr></table></div></div>",n.innerHTML=r;const d=document.getElementById("blue-selector");let s='\n    <div class="grid-wrapper">\n      <div class="grid-title">\n        <strong>Blue:</strong>\n      </div>\n      <div class="grid-input">\n        <input id="blue-input" value="" placeholder="Enter a value between 0-255" />\n      </div>\n      <div class="grid-slider">\n        <table id="green-slider" class="slider">\n          <tr>\n  ';for(let e=0;e<256;e++)s+=`<td style="background-color:rgb(0,0,${e});"></td>`;s+="</tr></table></div></div>",d.innerHTML=s}function eventListeners(){const e=document.getElementById("canvas"),t=new UI;e.addEventListener("click",()=>{t.randomColor(),t.setSelector(),t.clearData(),t.getData()})}document.addEventListener("DOMContentLoaded",()=>{createSelectors(),eventListeners()});