class UI{constructor(){this.CANVAS=document.getElementById("canvas"),this.DATA=document.getElementById("data"),this.RGB_ARR=[]}randomColor(){let e=Math.floor(256*Math.random()),t=Math.floor(256*Math.random()),n=Math.floor(256*Math.random());this.CANVAS.style.backgroundColor=`rgb(${e},${t},${n})`,this.RGB_ARR[0]=e,this.RGB_ARR[1]=t,this.RGB_ARR[2]=n}setSelector(){let e=document.getElementById("red-input"),t=document.getElementById("green-input"),n=document.getElementById("blue-input");e.value=this.RGB_ARR[0],t.value=this.RGB_ARR[1],n.value=this.RGB_ARR[2]}clearData(){for(this.DATA.classList.remove("show");this.DATA.hasChildNodes();)this.DATA.removeChild(this.DATA.childNodes[0])}getData(){this.DATA.classList.add("show"),this.getRgbData(),this.getHexData(),this.getHslData()}getRgbData(){let e=document.createElement("strong");e.innerHTML="RGB Code: ",this.DATA.appendChild(e);const t=document.createElement("div");t.innerHTML=this.CANVAS.style.backgroundColor,this.DATA.appendChild(t)}getHexData(){let e=this.RGB_ARR[0].toString(16).toUpperCase();1===e.length&&(e="0"+e);let t=this.RGB_ARR[1].toString(16).toUpperCase();1===t.length&&(t="0"+t);let n=this.RGB_ARR[2].toString(16).toUpperCase();1===n.length&&(n="0"+n);const d=document.createElement("strong");d.innerHTML="HEX Code: ",this.DATA.appendChild(d);const i=document.createElement("div");i.innerHTML="#"+e+t+n,this.DATA.appendChild(i)}getHslData(){const e=this.RGB_ARR[0]/255,t=this.RGB_ARR[1]/255,n=this.RGB_ARR[2]/255,d=Math.max(e,t,n),i=Math.min(e,t,n),s=d-i;let r;0===s?r=0:d===e?(r=(t-n)/s%6*60)<0&&(r+=360):d===t?(r=60*((n-e)/s+2))<0&&(r+=360):(r=60*((e-t)/s+4))<0&&(r+=360);let l,a=(d+i)/2;l=0===s?0:s/(1-Math.abs(2*a-1));const o=r.toFixed(0),c=(100*l).toFixed(0),g=(100*a).toFixed(0),h=document.createElement("strong");h.innerHTML="HSL Code: ",this.DATA.appendChild(h);const u=document.createElement("div");u.innerHTML=`hsl(${o}, ${c}%, ${g}%)`,this.DATA.appendChild(u)}}function createSelectors(){const e=document.getElementById("red-selector");let t='\n    <div class="grid-wrapper">\n      <div class="grid-title">\n        <strong>Red:</strong>\n      </div>\n      <div class="grid-input">\n        <input id="red-input" value="" placeholder="Enter a value between 0-255" />\n      </div>\n      <div class="grid-slider">\n        <table id="red-slider" class="slider">\n          <tr>\n  ';for(let e=0;e<256;e++)t+=`<td id="red-${e}" style="background-color:rgb(${e},0,0);"></td>`;t+="</tr></table></div></div>",e.innerHTML=t;const n=document.getElementById("green-selector");let d='\n    <div class="grid-wrapper">\n      <div class="grid-title">\n        <strong>Green:</strong>\n      </div>\n      <div class="grid-input">\n        <input id="green-input" value="" placeholder="Enter a value between 0-255" />\n      </div>\n      <div class="grid-slider">\n        <table id="green-slider" class="slider">\n          <tr>\n  ';for(let e=0;e<256;e++)d+=`<td style="background-color:rgb(0,${e},0);"></td>`;d+="</tr></table></div></div>",n.innerHTML=d;const i=document.getElementById("blue-selector");let s='\n    <div class="grid-wrapper">\n      <div class="grid-title">\n        <strong>Blue:</strong>\n      </div>\n      <div class="grid-input">\n        <input id="blue-input" value="" placeholder="Enter a value between 0-255" />\n      </div>\n      <div class="grid-slider">\n        <table id="green-slider" class="slider">\n          <tr>\n  ';for(let e=0;e<256;e++)s+=`<td style="background-color:rgb(0,0,${e});"></td>`;s+="</tr></table></div></div>",i.innerHTML=s}function eventListeners(){const e=document.getElementById("canvas"),t=new UI;e.addEventListener("click",()=>{t.randomColor(),t.setSelector(),t.clearData(),t.getData()})}document.addEventListener("DOMContentLoaded",()=>{createSelectors(),eventListeners()});